import styled from "styled-components";
import { MdKeyboardArrowRight, MdArrowForward } from "react-icons/md";

export const HeroContainer = styled.div`
  background: #0c0c0c;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 30px;
  height: 97vh;
  position: relative;
  z-index: 1;
  margin-top: -3rem;
  :before {
    content: "";
    position: absolute;
    top: 0;
    bottom: 0;
